package ique.daitechagent.adapters;import android.content.Context;import androidx.annotation.NonNull;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import ique.listviewchartitems.ChartItem;import java.util.List;import java.util.Objects;public class ChartDataAdapter extends ArrayAdapter<ChartItem> {  public ChartDataAdapter(Context context, List<ChartItem> objects) {    super(context, 0, objects);  }  public int getCount() {    return super.getCount();  }  @NonNull  public View getView(int position, View convertView, ViewGroup parent) {    return Objects.requireNonNull(getItem(position)).getView(position, convertView, getContext());  }  public int getItemViewType(int position) {    ChartItem ci = getItem(position);    if (ci != null) {      return ci.getItemType();    }    return 0;  }  public int getViewTypeCount() {    return 3;  }}