package ique.daitechagent.adapters;import android.content.Context;import androidx.recyclerview.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import android.widget.TextView;import ique.daitechagent.R;import ique.daitechagent.common.ClickListener;import ique.daitechagent.model.Dailyschedule;import java.util.ArrayList;import java.util.Iterator;import java.util.Locale;public class dailyscheduleListAdapter extends RecyclerView.Adapter<dailyscheduleListAdapter.ViewHolder> {	public ArrayList<Dailyschedule> arrayList = new ArrayList<>();	Context context;	public ArrayList<Dailyschedule> items;	ClickListener listener;	ViewHolder viewHolder;	class ViewHolder extends RecyclerView.ViewHolder implements OnClickListener {		ClickListener listener;		TextView vhcustomernamedaily;		TextView vhdatedaily;		TextView vhdescriptiondaily;		TextView vhperioddaily;		public ViewHolder(View itemView, ClickListener listener2) {			super(itemView);			this.vhcustomernamedaily = itemView.findViewById(R.id.lstcustomernameschedule);			this.vhdescriptiondaily = itemView.findViewById(R.id.lstscheduledescription);			this.vhperioddaily = itemView.findViewById(R.id.lstscheduleperiod);			this.vhdatedaily = itemView.findViewById(R.id.lstscheduledate);			this.listener = listener2;			itemView.setOnClickListener(this);		}		public void onClick(View view) {			this.listener.onClick(view, getAdapterPosition());		}	}	public dailyscheduleListAdapter(Context context2, ArrayList<Dailyschedule> items2, ClickListener listener2) {		this.context = context2;		this.items = items2;		this.listener = listener2;	}	public ViewHolder onCreateViewHolder(ViewGroup viewGroup, int i) {		this.viewHolder = new ViewHolder(LayoutInflater.from(this.context).inflate(R.layout.daily_schedule_history_template, viewGroup, false), this.listener);		return this.viewHolder;	}	public void onBindViewHolder(ViewHolder viewHolder2, int i) {		viewHolder2.vhcustomernamedaily.setText(this.items.get(i).getCustomername());		viewHolder2.vhdescriptiondaily.setText(this.items.get(i).getDescription());		viewHolder2.vhperioddaily.setText(this.items.get(i).getVisitPeriod());		viewHolder2.vhdatedaily.setText(String.valueOf(this.items.get(i).getVisitDate()));	}	public int getItemCount() {		return this.items.size();	}	public void filter(String characterText) {		String characterText2 = characterText.toLowerCase(Locale.getDefault());		this.items.clear();		if (characterText2.length() == 0) {			this.items.addAll(this.arrayList);		} else {			this.items.clear();			Iterator it = this.arrayList.iterator();			while (it.hasNext()) {				Dailyschedule dailyschedule = (Dailyschedule) it.next();				if (dailyschedule.getCustomername().toLowerCase(Locale.getDefault()).contains(characterText2) || dailyschedule.getDescription().toLowerCase(Locale.getDefault()).contains(characterText2) || dailyschedule.getVisitPeriod().toLowerCase(Locale.getDefault()).contains(characterText2)) {					this.items.add(dailyschedule);				}			}		}		notifyDataSetChanged();	}}